document.getElementById('cadastroForm').addEventListener('submit', async (e) => {
    e.preventDefault();

    const nome = document.getElementById('nome').value;
    const email = document.getElementById('email').value;
    const senha = document.getElementById('senha').value;
    const cpf = document.getElementById('cpf').value;
    const telefone = document.getElementById('telefone').value;
    const data_de_nascimento = document.getElementById('data_de_nascimento').value;

    try {
        const response = await fetch('http://localhost:3001/register', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                nome_cliente: nome,
                email_cliente: email,
                senha_cliente: senha,
                cpf_cliente: cpf,
                telefone_cliente: telefone,
                data_de_nascimento_cliente: data_de_nascimento,
            }),
        });

        if (!response.ok) {
            throw new Error('Erro ao registrar usuário');
        }

        const data = await response.json();
        alert('Cadastro bem-sucedido');
        localStorage.setItem('usuario', nome); // Armazena o nome do usuário
        window.location.href = './index.html'; // Redireciona para a página inicial
    } catch (error) {
        alert('Falha no cadastro: ' + error.message);
    }
});

// Atualiza o botão de login com o nome do usuário se estiver logado
const usuarioLogado = localStorage.getItem('usuario');
if (usuarioLogado) {
    document.querySelector('.botao-de-login').src = './navbar/user_icon.png';
    document.querySelector('.botao-de-login').alt = usuarioLogado;
    document.querySelector('.botao-de-login').title = usuarioLogado;
}
